import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import TheContent from './App';
import reportWebVitals from './reportWebVitals';

const intermediate =Array.from(document.getElementsByTagName('body'));
const theBody = intermediate[0];
const root = ReactDOM.createRoot(theBody);

function doOtherJS(){
    const divWrapper = theBody.children[0];
    theBody.removeChild(divWrapper);
    const stuffInWrapper = Array.from(divWrapper.children);
    stuffInWrapper.forEach(el => theBody.appendChild(el));
    const as = document.getElementsByTagName('a');

    const el = document.createElement('button')
    el.id = 'idk';
    el.innerHTML = 'click me'
    el.addEventListener('click', makeEventListener("SUp","moreSUp"));

    theBody.appendChild(el);
    const p = document.createElement('p');
    p.id = 'didotherjs';
    theBody.appendChild(p);
    console.log('did other js');
}


function makeEventListener(heading, articleContent){
    return async function(){
	console.log('clicked');
	await root.render((<TheContent heading={heading} articleContent={articleContent} />))
	if (! document.getElementById('didotherjs')){
	    doOtherJS();
	}
    };

}

root.render(
    (
      <TheContent
	  heading="hey"
	  articleContent="<div> sup </div>"/>)
);

doOtherJS();
